// src/pages/ContactUs.jsx
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';


export default function ContactUs() {
  const [date, setDate] = useState(new Date());

  const handleSubmit = e => {
    e.preventDefault();
    const name    = e.target.name.value;
    const email   = e.target.email.value;
    const message = e.target.message.value;
    const dateStr = date.toLocaleDateString(); 

    const subject = encodeURIComponent(`Contact Request from ${name}`);
    const body = encodeURIComponent(
      `Preferred Date: ${dateStr}\n` +
      `Name: ${name}\n` +
      `Email: ${email}\n\n` +
      `Message:\n${message}`
    );

    // replace with your actual contact email
    window.location.href = `mailto:hello@galaxyedgesafety.com?subject=${subject}&body=${body}`;
  };

  return (
    <main className="contact-section">
      <div className="contact-container">
        {/* Form Pane */}
        <article className="contact-form">
          <h1 className="contact-heading">Contact Us</h1>
          <p className="contact-subheading">
            Pick a date below and send us a message—let’s schedule a time to connect.
          </p>
          <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name</label>
            <input id="name" name="name" type="text" placeholder="Your name" required />

            <label htmlFor="email">Email</label>
            <input id="email" name="email" type="email" placeholder="you@example.com" required />

            <label htmlFor="message">Message</label>
            <textarea id="message" name="message" rows="4" placeholder="How can we help?" />

            <button type="submit" className="contact-submit">
              Send Message
            </button>
          </form>
        </article>

         {/* Calendar Pane */}
         <aside className="contact-calendar">
          <Calendar onChange={setDate} value={date} />
        </aside>
      </div>
    </main>
  );
}
