import React, { useEffect, useRef } from 'react';
import '../styles/VR.css';

export default function VR() {
  const bgRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!bgRef.current) return;

      const scrollY = window.scrollY;
      const vh = window.innerHeight;
      const progress = Math.min(scrollY / vh, 1);

      // scale down up to 80%
      const scale = 1 - progress * 0.2;
      // move up to -50% of its height
      const translateY = -progress * 50;

      bgRef.current.style.transform = `scale(${scale}) translateY(${translateY}%)`;
      bgRef.current.style.opacity   = `${1 - progress}`;
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="vr-page-container">
      {/* fixed, full-screen bg */}
      <div
        ref={bgRef}
        className="full-screen-background"
        style={{ backgroundImage: "url('/assets/VR-background.jpg')" }}
      />

      {/* all your VR content starts here */}
      <div className="vr-section-parallax-content">
        <h2 className="take-action-heading">
          Harnessing Virtual Reality for Workplace Safety
        </h2>
        <h3 className="vr-subheading">
          Revolutionize your safety training with immersive VR solutions designed to engage employees and minimize risks
        </h3>

        <div className="vr-details">
          <div className="vr-item">
            <h4>Interactive Safety Simulations</h4>
            <p>VR allows employees to interact with real-world scenarios…</p>
          </div>
          <div className="vr-item">
            <h4>Scalable and Flexible Training</h4>
            <p>Virtual reality enables scalable training programs…</p>
          </div>
          <div className="vr-item">
            <h4>Real-Time Feedback</h4>
            <p>Employees receive instant feedback during training sessions…</p>
          </div>
        </div>
      </div>
    </div>
  );
}
