                    className={`apple-form-question${q.input && q.id <= 6 ? ' text-inline' : ''}`}
                >
                    <h3 className="question-title">{q.text}</h3>

                    {q.id === 10 ? (
                    <>
                        <ul className="options-list">
                        {q.options.map(opt => (
                            <li key={opt} className="options-item">
                            <label>
                                <input
                                type="checkbox"
                                name={`question-${q.id}`}
                                value={opt}
                                checked={(answers[q.id] || []).includes(opt)}
