import React, { useState, useEffect, useRef } from "react";
import './App.css';
import About from "./pages/About"; // Import About Us section
import Header from './components/Header';  // Import Header
import { Link } from 'react-scroll'; // Import react-scroll for smooth scroll behavior

export default function App() {
  const [isHome, setIsHome] = useState(true);  // Track whether we're on Home or About Us section
  const aboutRef = useRef(null); // Reference for About Us section

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('in-view');
        } else {
          entry.target.classList.remove('in-view');
        }
      },
      { threshold: 0.5 } // Trigger when 50% of the element is in the viewport
    );

    if (aboutRef.current) {
      observer.observe(aboutRef.current);
    }

    return () => {
      if (aboutRef.current) {
        observer.unobserve(aboutRef.current);
      }
    };
  }, []);

  return (
    <div>
      {/* HEADER SECTION */}
      <Header />

      {/* Home Section */}
      {isHome ? (
        <div id="home">
          {/* Full-Screen Background Image with Gradient */}
          <div className="full-screen-background" style={{ backgroundImage: "url('/assets/HomePageBackground7.jpg')" }}>
            <div className="logo">
              <img src="/assets/logo.jpg" alt="GalaxyEDGE Safety Inc." className="logo-img" />
            </div>

            <div className="content">
              <h1 className="heading">
                Advancing Global Health <br />
                <span className="sub-heading"> & Safety Through Innovation</span>
              </h1>
              <br />
              <p className="subtext">
                Leveraging groundbreaking research, technology, partnerships, and<br />
                <span className="sub-subtext">innovation to transform occupational health & safety worldwide</span>
              </p>
              <br />
              {/* CTA Buttons */}
              <div className="cta-buttons">
                <button className="cta-button learn-more" onClick={() => setIsHome(false)}>Learn More</button>
                <button className="cta-button contact-us" onClick={() => setIsHome(false)}>Contact Us</button>
              </div>
            </div>
          </div>
        </div>
      ) : (
        // ABOUT US SECTION
        <div id="about-us" ref={aboutRef} className="about-us-section">
          <About />  {/* About Us Section */}
        </div>
      )}
    </div>
  );
}
