import React from "react";

const TakeAction = () => {

  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
    };

    // listen for scroll events
    window.addEventListener('scroll', handleScroll);

    // clean up
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // SHRINKING PART:
  const scaleFactor = 1 - scrollPosition / 4000; // Shrink the image as you scroll

  return (
      <div className="parallax-content">
        <h2 className="text-4xl font-bold mb-6">Take Action: Strengthen Workplace Safety & Workforce Resilience in an Evolving Global Landscape</h2>
        <p className="text">
            The rapidly changing economic and regulatory climate presents both challenges and opportunities for
            businesses navigating workforce mobility, compliance, and operational efficiency. With rising trade tensions,
            shifting labor demands, and increasing regulatory complexities, organizations must proactively invest in
            workforce development, digital transformation, and strategic risk management to remain competitive.
        </p>
      </div>
  );
};

export default TakeAction;
