/* src/components/GlobalMovementForm.jsx */
import React, { useState, useEffect, useRef, useCallback } from 'react';
import './GlobalMovementForm.css';

const images = [
  '/assets/image1.jpg',
  '/assets/image2.jpg',
  '/assets/image3.jpg',
  '/assets/image4.jpg'
];

const questions = [
  // Section 1
  {
    id: 'section-1',
    type: 'title',
    text: 'Contact Information'
  },
  {
    id: 1,
    text: 'Full Name',
    input: true,
    placeholder: 'Enter your Full Name'
  },
  {
    id: 2,
    text: 'Organization / Affiliation',
    input: true,
    placeholder: 'Enter your Organization/Affiliation’s name'
  },
  {
    id: 3,
    text: 'Job Title / Role',
    input: true,
    placeholder: 'Enter your Job Title / Role'
  },
  {
    id: 4,
    text: 'Country / Region',
    input: true,
    placeholder: 'eg: Canada'
  },
  {
    id: 5,
    text: 'Email',
    input: true,
    placeholder: 'eg: vpM0@example.com'
  },
  {
    id: 6,
    text: 'Phone Number (optional)',
    input: true,
    placeholder: 'Enter your Phone Number'
  },

  // Section 2
  {
    id: 'section-2',
    type: 'title',
    text: 'Primary Focus Areas'
  },
  {
    id: 10,
    text: 'Please select one or more key areas where your organization is seeking support:',
    options: [
      'Reduce incident or injury rates',
      'Leadership and culture transformation (includes strategy, business integration, and governance)',
      'Psychological health and psychosocial risk support',
      'Training – update, improve engagement or effectiveness',
      'Develop immersive or scenario-based learning',
      'Regulatory compliance',
      'Gap Analysis/Audit or compliance support (COR / ISO / CSA/ESG)',
      'Contractor or multi-site safety coordination',
      'Exploring AI tools to improve safety and operational efficiency',
      'Other'
    ]
  },

  // Section 3
  {
    id: 'section-3',
    type: 'title',
    text: 'Describe Your Current Challenge or Concern'
  },
  {
    id: 11,
    text: 'Please describe:',
    input: true,
    placeholder: 'e.g. We have high turnover due to safety incidents…'
  },

  // Section 4
  {
    id: 'section-4',
    type: 'title',
    text: 'Desired Outcomes'
  },
  {
    id: 12,
    text: 'What outcomes are you aiming for?',
    input: true,
    placeholder: 'e.g. 50% reduction in incidents within 6 months'
  },

  // Section 5
  {
    id: 'section-5',
    type: 'title',
    text: 'Urgency of Support Needed'
  },
  {
    id: 13,
    text: 'How soon do you need support or intervention?',
    options: [
      'Immediate (within 30 days)',
      'Short-term (1–3 months)',
      'Medium-term (3–6 months)',
      'Long-term or exploratory'
    ]
  },

  // Section 6
  {
    id: 'section-6',
    type: 'title',
    text: 'Interested in Exploring the PRIME360™ System Standard?'
  },
  {
    id: 14,
    options: [
      'Yes – for future alignment or development',
      'No – not at this time',
      'Possibly – would like more information'
    ]
  }
];

export default function GlobalMovementForm() {
  const [answers, setAnswers] = useState({});
  const [touched, setTouched] = useState(false);
  const [current, setCurrent] = useState(0);
  const timeoutRef = useRef(null);
  const delay = 5000;

  // Slideshow auto-advance
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent(prev => (prev + 1) % images.length);
    }, delay);
    return () => clearInterval(interval);
  }, []);

  // Handlers
  const handleCheckboxChange = (qId, opt) =>
    setAnswers(prev => {
      const curr = prev[qId] || [];
      return {
        ...prev,
        [qId]: curr.includes(opt) ? curr.filter(x => x !== opt) : [...curr, opt]
      };
    });

  const handleChange = (qId, value) =>
    setAnswers(prev => ({ ...prev, [qId]: value }));

  const goToSlide = idx => setCurrent(idx);

  const handleSubmit = useCallback(
    e => {
      e.preventDefault();
      const subject = encodeURIComponent(
        `OHS Request — ${answers[1] || 'Name'} / ${answers[2] || 'Organization'}`
      );
      let body = '';
      questions.forEach(q => {
        if (q.type !== 'title') {
          const ans = answers[q.id];
          if (Array.isArray(ans)) body += `${q.text}: ${ans.join(', ')}\n\n`;
          else if (ans) body += `${q.text}: ${ans}\n\n`;
        }
      });
      const mailto =
        `https://mail.google.com/mail/?view=cm&tf=1` +
        `&su=${subject}` +
        `&body=${encodeURIComponent(body)}`;
      window.open(mailto, '_blank');
    },
    [answers]
  );

  const onSubmit = e => {
    e.preventDefault();
    setTouched(true);
    if (!answers.agreeTerms || !answers.consentContact) return;
    handleSubmit(e);
  };

  return (
    <div className="GlobalMovementForm-container">
      {/* Hero & headings */}
      <div className="hero-heading">
        <h1>Join the Global Digital Technology Access Movement</h1>
      </div>
      <div className="Partnership-sub-heading">
        <h1>
          Working Together to Expand Access to Immersive Health &amp; Safety Learning
        </h1>
      </div>

      {/* Vision section */}
      <div className="OHS-section-header">
        <p>
          <strong>Our Vision</strong>
          <br />
          We envision a future where immersive health and safety education becomes a
          catalyst for lasting cultural transformation…
        </p>
      </div>

      <div className="Partnership-sub-heading">
        <h1>Global Task Force – Interest &amp; Contribution Form</h1>
      </div>

      {/* Form */}
      <form className="apple-form" onSubmit={onSubmit} noValidate>
        {questions.map(q =>
          q.type === 'title' ? (
            <h2 key={q.id} className="section-title">
              {q.text}
            </h2>
          ) : (
            <div
              key={q.id}
              className={`apple-form-question${
                q.input && q.id <= 6 ? ' text-inline' : ''
              }`}
            >
              <h3 className="question-title">{q.text}</h3>

              {q.options ? (
                q.id === 10 ? (
                  <>
                    <ul className="options-list">
                      {q.options.map(opt => (
                        <li key={opt} className="options-item">
                          <label>
                            <input
                              type="checkbox"
                              name={`question-${q.id}`}
                              value={opt}
                              checked={(answers[q.id] || []).includes(opt)}
                              onChange={() => handleCheckboxChange(q.id, opt)}
                            />
                            <span className="custom-checkbox" />
                            {opt}
                          </label>
                        </li>
                      ))}
                    </ul>
                    {(answers[10] || []).includes('Other') && (
                      <input
                        type="text"
                        name="question-10-other"
                        placeholder="Please specify"
                        value={answers['10-other'] || ''}
                        onChange={e =>
                          handleChange('10-other', e.target.value)
                        }
                        className="text-input"
                      />
                    )}
                  </>
                ) : (
                  <ul className="options-list">
                    {q.options.map(opt => (
                      <li key={opt} className="options-item">
                        <label>
                          <input
                            type="radio"
                            name={`question-${q.id}`}
                            value={opt}
                            checked={answers[q.id] === opt}
                            onChange={() => handleChange(q.id, opt)}
                          />
                          <span className="custom-radio" />
                          {opt}
                        </label>
                      </li>
                    ))}
                  </ul>
                )
              ) : (
                <input
                  type="text"
                  name={`question-${q.id}`}
                  placeholder={q.placeholder}
                  value={answers[q.id] || ''}
                  onChange={e => handleChange(q.id, e.target.value)}
                  className="text-input"
                />
              )}
            </div>
          )
        )}

        {/* Terms & Conditions */}
        <div
          className={`terms-section${
            touched && (!answers.agreeTerms || !answers.consentContact)
              ? ' terms-error'
              : ''
          }`}
        >
          <h3 className="terms-title">Consent &amp; Privacy Acknowledgement</h3>
          <p className="terms-text">
            By submitting this form, you agree that the information provided may be
            used by GalaxyEDGE Safety Inc. to respond to your inquiry…
          </p>
          <div className="terms-checkboxes">
            <label>
              <input
                type="checkbox"
                name="agreeTerms"
                checked={!!answers.agreeTerms}
                onChange={e => handleChange('agreeTerms', e.target.checked)}
              />
              I agree to the{' '}
              <a href="/terms-of-use" target="_blank" rel="noopener">
                Terms of Use
              </a>{' '}
              and{' '}
              <a href="/privacy-policy" target="_blank" rel="noopener">
                Privacy Policy
              </a>
            </label>
            <label>
              <input
                type="checkbox"
                name="consentContact"
                checked={!!answers.consentContact}
                onChange={e => handleChange('consentContact', e.target.checked)}
              />
              I consent to being contacted by GESI about this request
            </label>
          </div>
          {touched && (!answers.agreeTerms || !answers.consentContact) && (
            <p className="error-text">
              Please accept both the Terms of Use & Privacy Policy and consent to be
              contacted.
            </p>
          )}
        </div>

        <button type="submit" className="submit-button">
          Submit
        </button>
      </form>
    </div>
  );
}
