import React, { useState,useEffect, useRef } from 'react';
import { animateScroll as scroll } from 'react-scroll';
import { NavLink } from "react-router-dom";
import { Link } from 'react-scroll'; // Ensure you import Link from 'react-scroll'
import ReachOutNow from "./ReachOut";
import "../App.css"
import "../styles/Services.css"
import "../styles/AboutUS.css"
import '../styles/Prime360.css'
import '../styles/ImmersiveLearning.css'
import Footer from "./footer";

export default function ImmersiveLearning() {
  // Scroll to top on mount
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  // Scroll handler (optional, if used by a button)
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const sectionRef = useRef(null);
  const [visible, setVisible] = useState(false);
  const [expandedIndex, setExpandedIndex] = useState(null);

  useEffect(() => {
    const obs = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setVisible(true), 500);
          obs.disconnect();
        }
      },
      { threshold: 0.2 }
    );
    if (sectionRef.current) obs.observe(sectionRef.current);
    return () => obs.disconnect();
  }, []);

  // Open/close a cardâ€™s detail
  const openCard = (i) => {
    setExpandedIndex((prev) => (prev === i ? null : i));
  };

  // If a card is expanded, fetch its detail1/info1/image1
  const current = expandedIndex !== null ? cards[expandedIndex] : null;


  return (

    <div className = "ImmersiveLearning-container">
      <ReachOutNow/>

        {/* SCROLL UP ARROW */}
        <button
          className="scroll-up-arrow"
          onClick={scrollToTop}
          aria-label="Scroll to top"
        >
          <svg viewBox="0 0 16 16" aria-hidden="true">
            <path
              d="M4 10l4-4 4 4"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            />
          </svg>
        </button>

        {/* SCROLL DOWN ARROW */}
        <button
          className="scroll-down-arrow"
          onClick={() => scroll.scrollMore(600, { smooth: true, duration: 1000 })}
          aria-label="Scroll down 500px"
        >
          <svg viewBox="0 0 16 16" aria-hidden="true">
            <path
              d="M4 6l4 4 4-4"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            />
          </svg>
        </button>


        <div className="parallax-image">
        <img
          src="/assets/ImmersiveHero.jpg"
          alt="Main background"
          className="aboutUS-image-img"
        />
      </div>




      <div className = "ImmersiveLearning-Page-container">
        <div className = "ImmersiveLearningmain-heading">
            <h1>
              Immersive Learning: Revolutionizing Safety, Health & Well-being
            </h1>
        </div>



        <div className="section-header">
        <br />
          <p>
          At GalaxyEDGE Safety Inc., immersive learning isn't a trendâ€”it's a transformational tool embedded in how we drive behavioral change and culture-building across industries. Through PRIME360â„¢ and digital partnerships, we integrate these learning technologies into enterprise safety strategies to improve knowledge retention, increase engagement, and reduce incidents on the job
          <br />
          <br />  
            Aligned with ISO 45001, CSA Z1003, and COR, PRIME360â„¢ embeds executive accountability, risk controls, and continuous improvement into a single adaptable frameworkâ€”ensuring safety becomes a driver of organizational resilience, culture, and strategic growth.
          </p>
        </div>

        <div className = "Training-sub-heading">
            <h1>
            Where innovation meets impactâ€”reshaping how safety and well-being are learned            </h1>
        </div>

        <div className="Prime360-image">
            <img
            src="/assets/Prime360.jpg"
            alt="Main background"
            className="aboutUS-image-img"
            />
        </div>

        <div className="section-header">
        <br />
          <p>
          This enterprise-wide model integrates the best of global and national standardsâ€”including ISO 45001, CSA Z1003, and CORâ€”into a unified structure that advances both resilience and performance. Unlike traditional frameworks, PRIME360â„¢ places equal emphasis on leadership accountability, psychological safety, and strategic system integration.
          <br />
          <br />  
          Aligned with ISO 45001, CSA Z1003, and COR, PRIME360â„¢ embeds executive accountability, risk controls, and continuous improvement into a single adaptable frameworkâ€”ensuring safety becomes a driver of organizational resilience, culture, and strategic growth.
          </p>
        </div>

        <div className="Prime360-Structure-image">
            <img
            src="/assets/PRIME360_2.png"
            alt="Main background"
            className="aboutUS-image-img"
            />
        </div>

        <div className="Prime360-section-header">
        <br />
          <p>
            <strong>At the heart of PRIME360â„¢ are five interconnected pillars:</strong>   
          <br />
          <br />  
            â—¦ PRIME360â„¢ Acronym Graphic- Lisa to Forward           
           <br />  
            â—¦ PRIME360â„¢ Structural Table (5 Elements)- Lisa to Forward
            <br />
            â—¦ Summary Text Underneath Table Lisa to Forward          
            </p>
        </div>

        <div className="section-header">
            <br />
            <p>
            Together, these pillars form a dynamic, system that empowers organizations to shift from reactive safety practices to proactive, people-centered risk governance.
            <br />
            <br />  
            PRIME360â„¢ is more than a compliance toolâ€”itâ€™s a strategic asset designed to future-proof organizational safety, strengthen leadership, and drive meaningful results across health, wellbeing, and performance
            </p>
        </div>

        <NavLink to="/contact" smooth duration={900}>
            <button className="Training-CTA1"> Request a System Assessment Consultation</button>
        </NavLink>

      </div>
        <Footer/> 
    </div>
  );
}
