// src/pages/TakeAction.jsx
import React, { useEffect, useState, useRef } from "react";

const cards = [
  /* ROW 1 */
  {
    title: 'Strengthen Workplace Safety',
    text: 'Equip your teams with cutting-edge tools and training to reduce incidents and keep everyone protected.',
    details: 'Regular risk assessments and audits',
    info: [
      'Regular risk assessments and audits systematically identify potential workplace hazards before they turn into incidents.',
      'By evaluating processes, equipment, and behaviors on a scheduled basis, you stay ahead of emerging safety gaps.',
      'Audit findings drive continuous improvement—informing training updates, procedural tweaks, and new protective measures.',
      'Embedding these reviews into your culture shows a proactive commitment to employee well-being and compliance.',
    ],
    details: 'Regular risk assessments and audits',
    info: [
      'Regular risk assessments and audits systematically identify potential workplace hazards before they turn into incidents.',
      'By evaluating processes, equipment, and behaviors on a scheduled basis, you stay ahead of emerging safety gaps.',
      'Audit findings drive continuous improvement—informing training updates, procedural tweaks, and new protective measures.',
      'Embedding these reviews into your culture shows a proactive commitment to employee well-being and compliance.',
    ],

    img: '/assets/TakeActionImages/safety-gear.jpg',
  },
  {
    title: 'Enhance Workforce Resilience',
    text: 'Foster agile, up-skilled talent ready to tackle evolving challenges and drive productivity.',
    details: 'Cross-training, mental-health support, and flexible upskilling paths',
    info: [
      'Rotate roles so your team learns multiple skill sets.',
      'Offer on-demand micro-learning and resilience coaching.',
      'Provide mental-health tools and peer support groups.',
      'Track progress with personal development plans.',
    ],
    img: '/assets/TakeActionImages/workforce-resilience.jpg',
  },
  {
    title: 'Navigate Global Challenges',
    text: 'Stay ahead of regulatory shifts, geopolitical tensions, and economic uncertainties with confidence.',
    details: 'Scenario planning, risk modeling, and diversified partnerships',
    info: [
      'Run tabletop exercises for supply-chain disruptions.',
      'Use data models to stress-test market volatility.',
      'Forge local alliances to hedge geopolitical risks.',
      'Continuously update your risk register.',
    ],
    img: '/assets/TakeActionImages/global-challenges.jpg',
  },

  /* ROW 2 */
  {
    title: 'Optimize Operational Efficiency',
    text: 'Streamline processes and reduce waste through data-driven continuous improvement.',
    details: 'Lean methodologies, process mapping & KPI dashboards',
    info: [
      'Map workflows to uncover non-value steps.',
      'Deploy real-time dashboards for key metrics.',
      'Host Kaizen events for frontline insights.',
      'Tie rewards to measurable productivity gains.',
    ],
    img: '/assets/TakeActionImages/operational-efficiency.jpg',
  },
  {
    title: 'Leverage Digital Safety Tools',
    text: 'Adopt IoT sensors and mobile apps to monitor hazards and train employees in real-time.',
    details: 'Wearables, AR training & AI-powered analytics',
    info: [
      'Equip wearables for fatigue and exposure tracking.',
      'Use AR headsets for on-the-job walkthroughs.',
      'Analyze sensor data to predict failures.',
      'Integrate instant hazard-report apps.',
    ],
    img: '/assets/TakeActionImages/digital-safety-tools.jpg',
  },
  {
    title: 'Cultivate Safety Leadership',
    text: 'Empower supervisors and managers to champion a zero-injury culture every day.',
    details: 'Coaching, accountability frameworks & transparent comms',
    info: [
      'Run leadership workshops on positive reinforcement.',
      'Publish weekly safety KPIs in all-hands meetings.',
      'Spotlight “Safety Heroes” in your newsletter.',
      'Maintain open feedback loops for continuous learning.',
    ],
    img: '/assets/TakeActionImages/safety-leadership.jpg',
  },
];

const topRowCards = cards.slice(0, 3);
const bottomRowCards = cards.slice(3);

function Card({ card, index, visible, onClickCTA }) {
  return (
    <div
      className={`card${visible ? " fade-in" : ""}`}
      style={{ animationDelay: `${index * 0.5}s` }}
    >
      <div
        className="card-image"
        style={{ backgroundImage: `url(${card.img})` }}
      />
      <div className="card-content">
        <h3>{card.title}</h3>
        <p>{card.text}</p>
        <button className="card-cta" onClick={onClickCTA}>
          ＋
        </button>
      </div>
    </div>
  );
}

function DetailPanel({ card, onPrev, onNext, onClose }) {
  return (
    <div className="card-detail-section">
      <button className="detail-arrow left" onClick={onPrev}>
        ‹
      </button>

      <h3>{card.details}</h3>

      <div className="card-detail-row">
        <img src={card.img} alt={card.title} className="image1" />
        <ul className="card-detail-list">
          {card.info.map((pt, idx) => (
            <li key={idx}>{pt}</li>
          ))}
        </ul>
      </div>

      <button className="detail-arrow right" onClick={onNext}>
        ›
      </button>

      <button className="close-details" onClick={onClose}>
        ×
      </button>
    </div>
  );
}

export default function TakeAction() {
  const sectionRef = useRef(null);
  const [visible, setVisible] = useState(false);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const prevDetail = () =>
    setExpandedIndex((expandedIndex + cards.length - 1) % cards.length);
  const nextDetail = () =>
    setExpandedIndex((expandedIndex + 1) % cards.length);

  useEffect(() => {
    const obs = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setVisible(true), 500);
          obs.disconnect();
        }
      },
      { threshold: 0.2 }
    );
    if (sectionRef.current) obs.observe(sectionRef.current);
    return () => obs.disconnect();
  }, []);

  return (
    <section ref={sectionRef} className="take-action-section">
      <h2 className="take-action-heading">Take Action</h2>

      {/* Top Row */}
      <div className="cards-container">
        {topRowCards.map((c, idx) => (
          <Card
            key={idx}
            card={c}
            index={idx}
            visible={visible}
            onClickCTA={() =>
              setExpandedIndex(expandedIndex === idx ? null : idx)
            }
          />
        ))}
      </div>

      {/* Detail for Top Row */}
      {expandedIndex !== null && expandedIndex < 3 && (
        <DetailPanel
          card={cards[expandedIndex]}
          onPrev={prevDetail}
          onNext={nextDetail}
          onClose={() => setExpandedIndex(null)}
        />
      )}

      {/* Bottom Row */}
      <div className="cards-container">
        {bottomRowCards.map((c, idx) => {
          const realIdx = idx + 3;
          return (
            <Card
              key={realIdx}
              card={c}
              index={realIdx}
              visible={visible}
              onClickCTA={() =>
                setExpandedIndex(expandedIndex === realIdx ? null : realIdx)
              }
            />
          );
        })}
      </div>

      {/* Detail for Bottom Row */}
      {expandedIndex !== null && expandedIndex >= 3 && (
        <DetailPanel
          card={cards[expandedIndex]}
          onPrev={prevDetail}
          onNext={nextDetail}
          onClose={() => setExpandedIndex(null)}
        />
      )}
    </section>
  );
}
