import React, { useEffect, useRef, useState } from 'react';
import '../styles/VR.css';

export default function VR() {
  const bgRef = useRef(null);
  const sectionRef = useRef(null);

  // Array of hero background images
  const vrImages = [
    '/assets/VR-background.jpg',
    '/assets/VR-background-2.jpg',
    '/assets/VR-background-3.jpg',
    '/assets/VR-background-4.jpg',
  ];
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    if (!bgRef.current || !sectionRef.current) return;

    const sectionTop = sectionRef.current.offsetTop;
    const vh = window.innerHeight;
    const delayPx = 470;           // fixed-pixel delay before effect starts
    const scrollRange = vh * 2;    // span effect over two viewports

    // easing function for smooth deceleration
    const easeOutCubic = (t) => 1 - Math.pow(1 - t, 3);

    let ticking = false;
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const effective = scrollY - (sectionTop + delayPx);
      let p = effective / scrollRange;
      p = Math.min(Math.max(p, 0), 1);
      const eased = easeOutCubic(p);

      const scale = 1 - eased * 0.2;       // up to 80% size
      const translateY = -eased * 50;       // up to -50% translate
      const opacity = 1 - eased;           // fade out

      bgRef.current.style.transform = `scale(${scale}) translateY(${translateY}%)`;
      bgRef.current.style.opacity = `${opacity}`;
      ticking = false;
    };

    const onScroll = () => {
      if (!ticking) {
        window.requestAnimationFrame(handleScroll);
        ticking = true;
      }
    };

    window.addEventListener('scroll', onScroll, { passive: true });
    // run once to catch initial position
    handleScroll();

    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  // Navigation handlers for hero images
  const prevImage = () =>
    setCurrentImage((i) => (i + vrImages.length - 1) % vrImages.length);
  const nextImage = () =>
    setCurrentImage((i) => (i + 1) % vrImages.length);

  return (
    <div className="vr-page-container" ref={sectionRef}>
      {/* Full-screen, fixed background hero */}
      <div
        ref={bgRef}
        className="full-screen-background1"
        style={{ backgroundImage: `url('${vrImages[currentImage]}')` }}
      />

      {/* Image navigation arrows */}
      <button className="vr-arrow left" onClick={prevImage}>‹</button>
      <button className="vr-arrow right" onClick={nextImage}>›</button>

      {/* Dot index for images */}
      <div className="detail-index vr-image-index">
        {vrImages.map((_, idx) => (
          <span
            key={idx}
            className={`detail-dot${idx === currentImage ? ' active' : ''}`}
            onClick={() => setCurrentImage(idx)}
          />
        ))}
      </div>

      {/* VR section content */}
      <div className="vr-section-parallax-content">
        <h2 className="take-action-heading">
          Harnessing Virtual Reality for Workplace Safety
        </h2>
        <h3 className="vr-subheading">
          Revolutionize your safety training with immersive VR solutions designed to engage employees and minimize risks
        </h3>

        <div className="vr-details">
          <div className="vr-item">
            <h4>Interactive Safety Simulations</h4>
            <p>
              VR allows employees to interact with real-world scenarios in a controlled environment. From handling hazardous materials to learning emergency evacuation procedures, immersive simulations make training more effective and memorable.
            </p>
          </div>

          <div className="vr-item">
            <h4>Scalable and Flexible Training</h4>
            <p>
              Virtual reality enables scalable training programs that can be accessed anywhere, anytime. It's a cost-effective solution for large teams and businesses with remote workers.
            </p>
          </div>

          <div className="vr-item">
            <h4>Real-Time Feedback</h4>
            <p>
              Employees receive instant feedback during training sessions, helping them improve their decision-making skills and become more confident in handling various situations.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
