import React from "react";
import { Parallax } from "react-parallax"; // Import Parallax component from react-parallax

const About = () => {
  return (
    <Parallax
      className="parallax-container"
      bgImage="/assets/HomePageBackground2.jpg" // Ensure this is correct
      strength={800} // Adjust the intensity of the parallax effect
    >
      <div className="parallax-content">
        <h2 className="text-4xl font-bold mb-6">About Us</h2>
        <p className="text-lg leading-relaxed">
          GalaxyEDGE Safety Inc. is at the forefront of advancing health and safety protocols,
          leveraging cutting-edge technology and innovation to transform workplace safety.
          We understand the importance of protecting our workforce while ensuring operational efficiency
          and compliance in a rapidly evolving global landscape. Our solutions span regulatory compliance,
          workforce mobility, and digital safety training, tailored for industries across the globe.
        </p>
      </div>
    </Parallax>
  );
};

export default About;
