/* AppleLikeForm.jsx */
import React, { useState, useEffect, useRef } from 'react';
import './TargetedOHS.css';

const images = [
    '/assets/image1.jpg',
    '/assets/image2.jpg',
    '/assets/image3.jpg',
    '/assets/image4.jpg'
  ];

  const questions = [
    {
      id: 1,
      text: 'Organization Name',
      input: true,
      placeholder: 'Enter your organisation’s name'
    },
    {
      id: 2,
      text: 'Which operating system do you prefer?',
      options: ['iOS', 'macOS', 'iPadOS', 'watchOS', 'tvOS', 'Other']
    },
    {
      id: 3,
      text: 'Number of employees',
      input: true,
      placeholder: 'e.g. 250'
    },
    {
      id: 4,
      text: 'Which price range are you comfortable with?',
      options: ['$499–$699', '$700–$999', '$1000–$1299', '$1300–$1599', '$1600+', 'Not sure']
    },
    {
      id: 5,
      text: 'What motivates your purchase decision?',
      options: ['Brand Loyalty', 'Innovation', 'Recommendations', 'Reviews', 'Price', 'Design']
    },
    {
      id: 6,
      text: 'Where do you find product information?',
      options: ['Official Website', 'Social Media', 'Retail Store', 'Friends/Family', 'Tech Blogs', 'Forums']
    }
  ];

export default function TargetedOHS() {
    const [answers, setAnswers] = useState({});

    const handleChange = (qId, value) => {
      setAnswers(prev => ({ ...prev, [qId]: value }));
    };

  const [current, setCurrent] = useState(0);
  const timeoutRef = useRef(null);
  const delay = 5000; // 5 seconds

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent(prev => (prev + 1) % images.length);
    }, delay);
    return () => clearInterval(interval);
  }, []);

  const goToSlide = idx => {
    setCurrent(idx);
  };
  return (
    // SLIDESHOW:

    // QUESTIONS:
    <div className="OHS-container">
        <div className = "hero-heading">
          <h1>
            Request for Targeted OHS Support
          </h1>
        </div>

        <div className="section-header">
        <br />
          <p>
          At GalaxyEDGE Safety Inc. (GESI), we deliver more than safety consulting—we build
integrated strategies that lead to performance. Whether your organization is focused on
improving training, reducing incident rates, aligning with regulations, or leveraging AI
innovation, our targeted support model starts with understanding your specific needs.
Please complete this form to help us identify how we can best support your goals.          </p>
        </div>

        {/* <div className="slideshow">
        <div
            className="slideshow-slider"
            style={{ transform: `translateX(${-current * 100}%)` }}
        >
            {images.map((src, idx) => (
            <div className="slide" key={idx}>
                <img src={src} alt={`Slide ${idx + 1}`} />
            </div>
            ))}
        </div>
        <div className="slideshow-dots">
            {images.map((_, idx) => (
            <div
                key={idx}
                className={`slideshow-dot${current === idx ? ' active' : ''}`}
                onClick={() => goToSlide(idx)}
            />
            ))}
        </div>
        </div> */}


        <form className="apple-form">
      {questions.map(q => (
        <div key={q.id} className="apple-form-question">
          <h3 className="question-title">{q.text}</h3>
          {q.input ? (
            <input
              type="text"
              name={`question-${q.id}`}
              placeholder={q.placeholder}
              value={answers[q.id] || ''}
              onChange={e => handleChange(q.id, e.target.value)}
              className="text-input"
            />
          ) : (
            <ul className="options-list">
              {q.options.map(opt => (
                <li key={opt} className="options-item">
                  <label>
                    <input
                      type="radio"
                      name={`question-${q.id}`}
                      value={opt}
                      checked={answers[q.id] === opt}
                      onChange={() => handleChange(q.id, opt)}
                    />
                    <span className="custom-radio" />
                    {opt}
                  </label>
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
      <button type="submit" className="submit-button">Submit</button>
    </form>
    </div>
  );
}
