// src/components/ReachOutNow.jsx
import React, { Component } from 'react';
import { FiMail } from 'react-icons/fi';
import emailjs from 'emailjs-com';

export default class ReachOutNow extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open:   false,
      name:   '',
      email:  '',
      message:'',
      status: 'idle',
    };
  }

  // toggle the widget open/closed
  toggleOpen = () => {
    this.setState(({ open }) => ({ open }));
    this.setState(({ open }) => ({ open: !open }));
  };

  // handle form field changes
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  // send via EmailJS
  handleSubmit = (e) => {
    e.preventDefault();
    const { name, email, message } = this.state;
    this.setState({ status: 'sending' });

    emailjs
      .send(
        'YOUR_SERVICE_ID',   // ← replace with your EmailJS Service ID
        'YOUR_TEMPLATE_ID',  // ← replace with your EmailJS Template ID
        { from_name: name, from_email: email, message },
        'YOUR_USER_ID'       // ← replace with your EmailJS User ID
      )
      .then(() => {
        this.setState({
          status: 'sent',
          name:   '',
          email:  '',
          message:'',
        });
      })
      .catch((err) => {
        console.error('EmailJS error:', err);
        this.setState({ status: 'error' });
      });
  };

  render() {
    const { open, name, email, message, status } = this.state;

    return (
      <div className="reachout-container">
        {open && (
          <div className="reachout-widget">
            <div className="reachout-header">
              <h3>Galaxy Edge Safety</h3>
              <button
                className="reachout-close"
                onClick={this.toggleOpen}
                aria-label="Close"
              >
                ×
              </button>
            </div>
            <div className="reachout-body">
              {status === 'sent' ? (
                <p>✅ Thanks for reaching out! We’ll be in touch shortly.</p>
              ) : (
                <>
                  <p className="reachout-intro">
                    Hi! Let us know how we can help and we’ll respond shortly.
                  </p>
                  <form
                    className="reachout-form"
                    onSubmit={this.handleSubmit}
                  >
                    <input
                      name="name"
                      type="text"
                      placeholder="Name*"
                      value={name}
                      onChange={this.handleChange}
                      required
                    />
                    <input
                      name="email"
                      type="email"
                      placeholder="Email*"
                      value={email}
                      onChange={this.handleChange}
                      required
                    />
                    <textarea
                      name="message"
                      placeholder="How can we help?*"
                      value={message}
                      onChange={this.handleChange}
                      required
                    />
                    <button
                      type="submit"
                      className="reachout-send"
                      disabled={status === 'sending'}
                    >
                      {status === 'sending' ? 'Sending…' : 'SEND'}
                    </button>
                    {status === 'error' && (
                      <p style={{ color: 'red', marginTop: 8 }}>
                        Oops, something went wrong.
                      </p>
                    )}
                  </form>
                  <small className="reachout-recaptcha">
                    This site is protected by reCAPTCHA and the Google Privacy
                    Policy and Terms of Service apply.
                  </small>
                </>
              )}
            </div>
          </div>
        )}

        {!open && (
          <button
            className="reachout-toggle"
            onClick={this.toggleOpen}
            aria-label="Reach Out Now"
          >
            <FiMail size={28} strokeWidth={2.3} color="#000" />
          </button>
        )}
      </div>
    );
  }
}
