import React, { useState, useEffect, useRef } from "react";
import './App.css';
import About from "./pages/About"; // Import About Us section
import Home from "./pages/Home"; // Import About Us section
import Header from './components/Header';  // Import Header
import { Parallax } from 'react-parallax'; // Import Parallax component



export default function App() {
  /* 
  -----------------------------------------------------------------------------------------------
  *************************************** STATE VARIABLES ***************************************
  -----------------------------------------------------------------------------------------------
  */ 


  /* 
  -----------------------------------------------------------------------------------------------
  *************************************** CONST FUNCTIONS ***************************************
  -----------------------------------------------------------------------------------------------
  */ 

  /* 
  -----------------------------------------------------------------------------------------------
  ************************************* USE EFFECT FUNCTIONS ************************************
  -----------------------------------------------------------------------------------------------
  */ 


  /* 
  -----------------------------------------------------------------------------------------------
  ************************************** RETURN STATEMENTS **************************************
  -----------------------------------------------------------------------------------------------
  */ 
  return (
    <div>
      {/* HEADER SECTION */}
      <Header />

        <Parallax
          className="full-screen-background" // Ensure this class has proper styling in App.css
          bgImage="/assets/HomePageBackground2.jpg" // Background image for home section
          strength={500} // Parallax strength (adjust as needed)
        ></Parallax>

      {/* Always render the About Us section with the Parallax effect */}
      <div id="home">
        {/* Directly render the About component */}
        <Home />  {/* About Us Section */}
      </div>


      {/* Always render the About Us section with the Parallax effect */}
      <div id="about-us" className="about-us-section">
        {/* Directly render the About component */}
        <About />  {/* About Us Section */}
      </div>

      <Parallax
          className="full-screen-background" // Ensure this class has proper styling in App.css
          bgImage="/assets/HomePageBackground6.jpg" // Background image for home section
          strength={500} // Parallax strength (adjust as needed)
        ></Parallax>
    </div>
  );
}
