import React, { useState,useEffect, useRef } from 'react';
import { animateScroll as scroll } from 'react-scroll';
import { NavLink } from "react-router-dom";
import { Link } from 'react-scroll'; // Ensure you import Link from 'react-scroll'
import ReachOutNow from "./ReachOut";
import "../App.css"
import "../styles/Training.css"
import "../styles/AboutUS.css"
import Footer from "../pages/footer";

const cards = [
  {
    title: "Targeted Support for Safety Gaps",
    img: "/assets/services1.jpg",
    subtitle: "Tackle pressing occupational health and safety issues with focused, data-informed strategies",
    info1: [
      "We work closely with businesses to address specific OHS pain points—whether it's reducing incident rates, improving mental health response, or resolving compliance shortfalls. Our experts deliver tailored solutions backed by behavioral insights, digital diagnostics, and best-practice benchmarking to produce rapid, sustainable improvements"
    ],    
  },
  {
    title: "Build a Safer System from the Ground Up",
    img: "/assets/services2.jpg",
    subtitle: "Assess and align your safety systems with governance frameworks for lasting cultural impact",
    info1: [
      "Our comprehensive assessments evaluate your full health and safety ecosystem—policies, processes, leadership practices, and cultural alignment—against recognized frameworks like ISO 45001, COR, and ESG requirements. We provide actionable roadmaps that strengthen governance, enhance accountability, and embed resilience at every level"
    ],    
  }
]
export default function Training() {
  // Scroll to top on mount
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  // Scroll handler (optional, if used by a button)
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const sectionRef = useRef(null);
  const [visible, setVisible] = useState(false);
  const [expandedIndex, setExpandedIndex] = useState(null);

  useEffect(() => {
    const obs = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setVisible(true), 500);
          obs.disconnect();
        }
      },
      { threshold: 0.2 }
    );
    if (sectionRef.current) obs.observe(sectionRef.current);
    return () => obs.disconnect();
  }, []);

  // Open/close a card’s detail
  const openCard = (i) => {
    setExpandedIndex((prev) => (prev === i ? null : i));
  };

  // If a card is expanded, fetch its detail1/info1/image1
  const current = expandedIndex !== null ? cards[expandedIndex] : null;


  return (

    <div className = "Services-container">
      <ReachOutNow/>

        {/* SCROLL UP ARROW */}
        <button
          className="scroll-up-arrow"
          aria-label="Scroll to top"
        >
          <svg viewBox="0 0 16 16" aria-hidden="true">
            <path
              d="M4 10l4-4 4 4"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            />
          </svg>
        </button>

        {/* SCROLL DOWN ARROW */}
        <button
          className="scroll-down-arrow"
          onClick={() => scroll.scrollMore(600, { smooth: true, duration: 1000 })}
          aria-label="Scroll down 500px"
        >
          <svg viewBox="0 0 16 16" aria-hidden="true">
            <path
              d="M4 6l4 4 4-4"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            />
          </svg>
        </button>


      <div className="parallax-image">
        <img
          src="/assets/VR-image1.jpg"
          alt="Main background"
          className="aboutUS-image-img"
        />
      </div>


      <div className = "Page-container">
        <div className = "main-heading">
            <h1>
            People-Powered Safety: Strategy That Starts From Within
            </h1>
        </div>

        <div className="section-header">
        <br />
          <p>
          We go beyond traditional consulting. Our Safety Intelligence & Advisory services deliver strategic guidance through tailored gap analyses aligned with national and international frameworks—such as ISO 45001, the National Standard for Psychological Health and Safety, and COR certification.
          <br />
          <br />  
          But we don’t stop at systems and standards. We help organizations uncover hidden risks and define clear, people-centered roadmaps that embed safety into culture, leadership, and daily operations—enhanced by intelligent digital tools and emerging technologies. From frontline engagement to executive alignment, we empower leaders with the insights needed to drive meaningful, lasting change.
          </p>
        </div>

        {/* Cards Grid */}
        <div className="services-cards-container"> 
          {cards.map((card, i) => {
            const isActive = expandedIndex === i;
            return (
              <div
                key={i}
                className={[
                  "services-card",
                  visible && "fade-in",
                  isActive && "hovered",
                ]
                  .filter(Boolean)
                  .join(" ")}
                style={{ animationDelay: `${i * 0.5}s` }}
              >
                <div
                  className="services-card-image"
                  style={{ backgroundImage: `url(${card.img})` }}
                />
                <div className="card-content">
                  <h3>{card.title}</h3>
                  <p>{card.subtitle}</p>
                  {card.text && <p>{card.text}</p>}

                  <button
                    className={`card-cta${isActive ? " active" : ""}`}
                    onClick={() => openCard(i)}
                  >
                    <span className="icon">
                      {isActive ? (
                        /* When active, show the “+” sign (rotated via CSS) */
                        "+"
                      ) : (
                        /* When not active, show your SVG‐based scroll button instead */
                        <button
                          className="scroll-down-arrow-card"

                          aria-label="Scroll down 600px"
                        >
                          <svg viewBox="0 0 16 16" aria-hidden="true">
                            <path
                              d="M4 6l4 4 4-4"
                              fill="none"
                              stroke="currentColor"
                              strokeWidth="2"
                            />
                          </svg>
                        </button>
                      )}
                    </span>
                  </button>
                </div>
              </div>
            );
          })}
        </div>

        {/* Detail Section: only shows detail1/info1/image1 */}
        {current && (
          <div className="card-detail-section">

              <ul className="card-detail-list">
                {current.info1.map((pt, idx) => (
                  <li key={idx}>{pt}</li>
                ))}
              </ul>
          </div>
        )}

      </div>
        <Footer/> 
    </div>
  );
}
