    html += `
    <h2 style="font-size:22px;margin:24px 0 8px;">${label}</h2>
    <p style="margin:4px 0 16px;font-size:14px;">${answers[id]||''}</p>
    `;
    });

    // Urgency (13)
    html += `
    <h2 style="font-size:22px;margin:24px 0 8px;">Urgency of Support Needed</h2>
    <p style="margin:4px 0 16px;font-size:14px;">${answers[13]||''}</p>
