    </ul>
    </div>
    `;

    // 2) Build MIME message with HTML only
    const boundary = 'my_boundary_123';
    const rawParts = [
    `--${boundary}`,
    'Content-Type: text/html; charset="UTF-8"',
    'Content-Transfer-Encoding: 7bit',
    '',
    html,
    `--${boundary}--`
    ];
    const emailLines = [
    'To: recipient@example.com',
    'Subject: New Targeted OHS Support Request',
    'MIME-Version: 1.0',
    `Content-Type: multipart/alternative; boundary="${boundary}"`,
    '',
    rawParts.join('\r\n')
    ];
    const emailRaw = emailLines.join('\r\n');

    // 3) Base64url encode
    const encodedEmail = btoa(unescape(encodeURIComponent(emailRaw)))
    .replace(/\+/g,'-').replace(/\//g,'_').replace(/=+$/,'');

    // 4) Send via Gmail API
    if (!gapi.auth2.getAuthInstance().isSignedIn.get()) {
    await gapi.auth2.getAuthInstance().signIn();
    }
    await gapi.client.gmail.users.messages.send({
    userId: 'me',
    resource: { raw: encodedEmail }
    });

alert('Email sent with rich HTML formatting!');
