// src/components/ReachOutNow.jsx
import React, { useState } from 'react';
import { FiMail } from 'react-icons/fi';        // mail/envelope icon

export default function ReachOutNow() {
  const [open, setOpen] = useState(false);

  // prebuilt Gmail compose URL
  const gmailUrl =
    'https://mail.google.com/mail/?view=cm&fs=1&to=lamboferrari2025@gmail.com';

  // intercept form submit, open Gmail in new tab
  const handleSubmit = (e) => {
    e.preventDefault();
    window.open(gmailUrl, '_blank', 'noopener');
  };

  return (
    <div className="reachout-container">
      {open && (
        <div className="reachout-widget">
          <div className="reachout-header">
            <h3>Galaxy Edge Safety</h3>
            <button
              className="reachout-close"
              onClick={() => setOpen(false)}
              aria-label="Close"
            >
              ×
            </button>
          </div>
          <div className="reachout-body">
            <p className="reachout-intro">
              Hi! Let us know how we can help and we’ll respond shortly.
            </p>
            <form className="reachout-form" onSubmit={handleSubmit}>
              <input type="text" placeholder="Name*" required />
              <input type="email" placeholder="Email*" required />
              <textarea placeholder="How can we help?*" required />
              <button type="submit" className="reachout-send">
                SEND
              </button>
            </form>
            <small className="reachout-recaptcha">
              This site is protected by reCAPTCHA and the Google Privacy Policy
              and Terms of Service apply.
            </small>
          </div>
        </div>
      )}

      {!open && (
        <button
          className="reachout-toggle"
          onClick={() => setOpen(true)}
          aria-label="Reach Out Now"
        >
          <FiMail size={28} color="#000000" strokeWidth={2.3} />
        </button>
      )}
    </div>
  );
}
