import React, { useState } from "react";
import './App.css';
import About from "./pages/About"; // Import About Us section
import Header from './components/Header';  // Import Header


export default function App() {

/* 
-----------------------------------------------------------------------------------------------
*************************************** STATE VARIABLES ***************************************
-----------------------------------------------------------------------------------------------
*/ 

  const [isHome, setIsHome] = useState(true);

/* 
-----------------------------------------------------------------------------------------------
*************************************** CONST FUNCTIONS ***************************************
-----------------------------------------------------------------------------------------------
*/ 

  const showHomePage = () => {
    setIsHome(true);
  };
  const showAboutPage = () => {
    setIsHome(false);
  }

/* 
-----------------------------------------------------------------------------------------------
************************************** RETURN STATEMENTS **************************************
-----------------------------------------------------------------------------------------------
*/ 
return (
  <div>
    {/* HEADER SECTION */}
    <Header />

    {/* CONDITIONAL RENDERING FOR IS-HOME */}
    {isHome ? (
      <div id="home"> {/* Ensure Home section has an id */}
        {/* Full-Screen Background Image with Gradient */}
        <div className="full-screen-background" style={{ backgroundImage: "url('/assets/HomePageBackground2.jpg')" }}>
          <div className="logo">
            <img src="/assets/logo.jpg" alt="GalaxyEDGE Safety Inc." className="logo-img" />
          </div>

          <div className="content">
            <h1 className="heading">
              Advancing Global Health <br />
              <span className="sub-heading"> & Safety Through Innovation</span>
            </h1>
            <br />

            <p className="subtext">
              Leveraging groundbreaking research, technology, partnerships, and<br />
              <span className="sub-subtext">innovation to transform occupational health & safety worldwide</span>
            </p>
            <br />
            <div className="cta-buttons">
              <button className="cta-button learn-more">Learn More</button>
              <button className="cta-button contact-us">Contact Us</button>
            </div>
          </div>
        </div>
      </div>
    ) : (
      <div id="about-us">
        <About />  {/* Render About Us section when isHome is false */}
      </div>
    )}
  </div>
);
}
