import React, { useEffect, useState, useRef } from "react";



const cards = [
  // ROW 1
  {
    title: 'Strengthen Workplace Safety',
    text: 'Equip your teams with cutting-edge tools and training to reduce incidents and keep everyone protected.',
    details: 'Regular risk assessments and audits',
    info: [
    ' Regular risk assessments and audits systematically identify potential workplace hazards before they lead to incidents.',
    'By evaluating processes, equipment, and behaviors on a scheduled basis, organizations stay ahead of emerging safety gaps',
    'Audit findings drive continuous improvement—justifying training updates, procedural tweaks, and new protective measures.',
    'Embedding these reviews into your culture demonstrates a proactive commitment to employee well-being and compliance'
    ],        
    img: '/assets/TakeActionImages/safety-gear.jpg',
  },
  {
    title: 'Enhance Workforce Resilience',
    text: 'Foster agile, up-skilled talent ready to tackle evolving challenges and drive productivity.',
    img: '/assets/TakeActionImages/workforce-resilience.jpg',
  },
  {
    title: 'Navigate Global Challenges',
    text: 'Stay ahead of regulatory shifts, geopolitical shifts, and economic uncertainties with confidence.',
    img: '/assets/TakeActionImages/global-challenges.jpg',
  },

  // ROW 2

  {
    title: 'Optimize Operational Efficiency',
    text: 'Streamline processes and reduce waste through data-driven continuous improvement.',
    details: 'Implement Lean methodologies, process mapping, and KPIs to uncover and eliminate bottlenecks.',
    info: [
      'Map key workflows to identify non-value-added steps.',
      'Use real-time dashboards to track efficiency metrics.',
      'Hold regular Kaizen events for frontline input.',
      'Tie incentives to measurable productivity gains.'
    ],
    img: '/assets/TakeActionImages/operational-efficiency.jpg',
  },

  {
    title: 'Leverage Digital Safety Tools',
    text: 'Adopt IoT sensors and mobile apps to monitor hazards and train employees in real-time.',
    details: 'Deploy wearables, AR training modules, and AI analytics to predict and prevent incidents.',
    info: [
      'Equip workers with wearable monitors for fatigue and exposure.',
      'Use AR headsets for on-the-job safety coaching.',
      'Analyze sensor data to forecast machine failures.',
      'Integrate apps for instant hazard reporting.'
    ],
    img: '/assets/TakeActionImages/digital-safety-tools.jpg',
  },
  {
    title: 'Cultivate Safety Leadership',
    text: 'Empower supervisors and managers to champion a zero-injury culture every day.',
    details: 'Train leaders in coaching techniques, accountability systems, and transparent communications.',
    info: [
      'Run leadership workshops on positive reinforcement.',
      'Set clear safety KPIs and review them weekly.',
      'Celebrate “safety heroes” in all-hands meetings.',
      'Maintain open feedback loops for continuous learning.'
    ],
    img: '/assets/TakeActionImages/safety-leadership.jpg',
  },
];

export default function TakeAction() {
  const sectionRef = useRef(null);
  const [visible, setVisible] = useState(false);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const prevDetail = () => {
    setExpandedIndex((expandedIndex + cards.length - 1) % cards.length);
  };

  const nextDetail = () => {
    setExpandedIndex((expandedIndex + 1) % cards.length);
  };

  useEffect(() => {
    const obs = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setVisible(true), 500);
          obs.disconnect();
        }
      },
      { threshold: 0.2 }
    );
    if (sectionRef.current) obs.observe(sectionRef.current);
    return () => obs.disconnect();
  }, []);

  return (
    <section ref={sectionRef} className="take-action-section">
      <h2 className="take-action-heading">Take Action</h2>

      <div className="cards-container">
        {cards.map((c, i) => (
          <Fragment key={i}>
            <div
              className={`card${visible ? " fade-in" : ""}`}
              style={{ animationDelay: `${i * 0.5}s` }}
            >
              <div
                className="card-image"
                style={{ backgroundImage: `url(${c.img})` }}
              />
              <div className="card-content">
                <h3>{c.title}</h3>
                <p>{c.text}</p>
                <button
                  className="card-cta"
                  onClick={() =>
                    setExpandedIndex(expandedIndex === i ? null : i)
                  }
                >
                  ＋
                </button>
              </div>
            </div>

            {/* After every third card, render the detail panel for that row */}
            {i % 3 === 2 &&
              expandedIndex !== null &&
              Math.floor(expandedIndex / 3) === Math.floor(i / 3) && (
                <div className="card-detail-section">
                  <button className="detail-arrow left" onClick={prevDetail}>
                    ‹
                  </button>

                  <h3>{cards[expandedIndex].details}</h3>

                  <div className="card-detail-row">
                    <img
                      src="/assets/TakeActionImages/AdditionalinfoImages/Risk assesment1.jpg"
                      alt="second"
                      className="image1"
                    />
                    <ul className="card-detail-list">
                      {cards[expandedIndex].info.map((pt, idx) => (
                        <li key={idx}>{pt}</li>
                      ))}
                    </ul>
                  </div>

                  <button className="detail-arrow right" onClick={nextDetail}>
                    ›
                  </button>

                  <button
                    className="close-details"
                    onClick={() => setExpandedIndex(null)}
                  >
                    ×
                  </button>
                </div>
              )}
          </Fragment>
        ))}
      </div>
    </section>
  );
}
